Task.Script:
if IsDef('Calc') and not Process.FileToPID( 'calc.exe' )  then
begin  
    Calc;
    Write('Calc execute');
end else 
    Write('Calc not execute');

if IsDef('ProceXP') and not Process.FileToPID( 'procexp64.exe' )  then
begin  
    ProceXP;
    Write('ProceXP execute');
end else 
    Write('ProceXP not execute');

if IsDef('IType') and not Process.FileToPID( 'itype.exe' )  then
begin  
    IType;
    Write('IType execute');
end else 
    Write('IType not execute');

if IsDef('Netlimiter') and not Process.FileToPID( 'NLClientApp.exe' )  then
begin  
    Netlimiter.Parameter:='/tray';
    Netlimiter;
    Write('Netlimiter execute');
end else 
    Write('Netlimiter not execute');

File.Delete('D:\_Users\Administrator\AppData\Local\Temp\*.*', 1044 );

if IsDef('WPRun') then
begin
    WPRun;
    Write('WPRun execute');
end else 
    Write('WPRun not execute');

System.FileListMax  :=  100 ;
System.LoopLimite := 1e10 ;
System.CanClose := 1;
System.CanOff := 0;

FrmPGofer.Left := 20;
FrmPGofer.Top := 20;
FrmPGofer.Hide;
